<?phpclass secuenceModel extends Model {public function __construct() {parent::__construct();}public function get($nombre) {		$datos = $this->_db->query("SELECT nextval('{$nombre}') as id");				if ($datos)		return $datos->fetch();}public function set($nombre) {		$datos = $this->_db->query("INSERT INTO sequence_data (sequence_name) VALUES ({$nombre})");		if ($datos)	return $datos->fetchAll();}}?> 